import {  ACTIONS } from "./../actions/actions.js";


const deck = {
    track : {
        title : "",
        bmp : 0,
        quality : "",
        duration : "",
        source : "",
        id: ""
    }, // 
    state : {
        paused: true,
        cue: 0,
        pitch: 0,
        position : 0
    }
}

const chanel = {
    volume: 100,
    low: 100,
    mid : 100,
    high : 100,
    gain: 100
}

const initState = {
    deck: {
        A:{
            ...deck
        },
        B:{
            ...deck
        }
    },
   
    mikser : {
        chanelA : {
            ...chanel,
        },
        chanelB : {
            ...chanel,
        },
        fader : {
            position : 50 //A: 0 <-> B:100
        }
    }
}

function consoleReducer(state = initState, action){
    switch(action.type){
        case ACTIONS.LOAD_TRACK : {
            let destination = action.destination;
            let track = action.track;
            let newState = {...state};
            if(newState.deck[destination]){
                newState.deck[destination].track = {
                    title : track.title,
                    bmp : track.bpm || 0,
                    quality : track.quality,
                    duration : track.duration,
                    source : track.source,
                    id : track.id
                }
            }
            return newState;
        }

        default : return state;
    }
}

export default consoleReducer;
