import React, { useState, useRef, useEffect, useCallback } from "react";
import {formater} from "./../../../../../utils/time/timeFromater";
import "./progress-slider.scss"

//horizontal
const ProgressSlider = ({player}) => {
    const [progress, setProgress] = useState(0);
    const [currentRecord, setCurrentRecord] = useState(null);
    const [position,  setPosition] = useState();
    const [currentTime, setCurrentTime] = useState(0);

    const [currentDuration, setCurrentDuration] = useState(0);
    
    const sliderArea = useRef(null);
    const sliderThumb = useRef(null);
    const sliderProgress = useRef(null);
    const sliderBufferd = useRef(null);

    //dementions 
    const [sliderAreaWidth, setSliderAreaWidth] = useState(1)

    useEffect(()=>{
      //  console.log(progress)
       setCurrentTime(currentDuration * progress)
    }, [progress , setCurrentTime])

    useEffect(()=>{
        console.log(currentRecord)
        if(currentRecord){
           setCurrentDuration(currentRecord.duration);
        }

    }, [currentRecord, setCurrentDuration])

    useEffect(()=>{
        if(!player){
            return;
        }
        player.addProgressWatcher('#', setProgress);
        player.addCurrentWatcher(setCurrentRecord);
    }, [player])


    const thumbMouseDown = useCallback((e)=>{
        console.log('clikc;', e)
    }, [])

    const setThumb = useCallback((ref)=>{
        //sliderThumb(ref);
        sliderThumb.current = ref;
        ref.addEventListener('mousedown', (e)=> thumbMouseDown(e))
    }, [sliderThumb])

    const setSliderArea = useCallback((ref) => {
        sliderArea.current = ref;
        const width = ref.getBoundingClientRec().width;
        setSliderAreaWidth(width);
    }, [sliderArea])

    return (
        <div className="seek-slider">
            <div className="record-details-time time-current">
                 <span>{formater.secondsToStr(currentTime / 1000)}</span>
            </div>
            <div className="slider-active-area" ref={setSliderArea}>
                <div className="slider-thumb" ref={setThumb} draggable="false"/>
                <div className="slider-progress" ref={sliderProgress}/>
                <div className="slider-buffered" ref={sliderBufferd}/>
            </div>
            <div className="record-details-time time-duration">
                <span>{formater.secondsToStr(currentDuration/1000)}</span>
            </div>
        </div>
    
    )
}

export default ProgressSlider;
