import React, { useCallback, useMemo, useState, useEffect } from 'react';
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faAngleDoubleLeft, faAngleDoubleRight } from "@fortawesome/free-solid-svg-icons";
import UUID from 'uuidjs'
import icons from "./emoji.json";
const iconsOnPanel = 30;

const buttonStyle = {
    background: 'transparent',
    border: 'none',
    boxShadow: 'none',
    outline: 'none',
    width: '16.6%',
    height: '20%'
}

const sectionStyle = {
    width: '100%',
    height: '100%',
    display: 'inline-block',
    verticalAlign: 'top',
    whiteSpace: 'normal'
}

const slideContainer = {
    overflow: 'hidden',
    width: '210px',
    whiteSpace: 'nowrap',
    display: 'inline-block',
    position: 'sticki',
    bottom: '50px',
    transform: '',

}

const containerStyle = {
    position: 'absolute',
    //display: 'inline-block',
    transform: 'translate(-150px ,-185px)',
    width: '250px',
    height: '130px',
    background: 'white',
    boxSizing: 'content-box',
    padding: '5px',
}

const sliderNavigationButton = {
    ...buttonStyle,
    display: 'inline-block',
    width: '20px',
    height: '100%',
    top: '50%',
    verticalAlign: 'top'
}

const iconStyle = {
    transform: 'translateX(-25%)',
}

const EmotiContainer = ({ onSelect, toggle }) => {
    const [inconPanels , setPanels] = useState(null);

    const inconsPanels = useCallback(() => {
        let panels = [];
        for (let i = 0; i < icons.length; i += iconsOnPanel) {
            let panel = [];
            for (let j = i; (j < iconsOnPanel + i) && (i + j < icons.length); j++) {
                let icon = icons[j]
                panel.push(
                    <button key={'icon-' + (i + j)} style={buttonStyle} onClick={onSelect.bind(null, icon.emoji)}>{icon.emoji}</button>
                )
            }
            panels.push(
                <section style={sectionStyle} className="emoticons-panel" key={'pannel-' + i}>
                    {panel}
                </section>
            )
        }
        return panels;
    }, [])

    const [current, setCurrent] = useState(0);

    const next = useCallback((event) => {
        event.preventDefault();
        setCurrent(c => {
            if (c + 1 < inconPanels.length) {
                return c + 1;
            }
            return c;
        });
    }, [inconPanels])

    const prev = useCallback((event) => {
        event.preventDefault();
        setCurrent(c => {
            if (c - 1 > 0) {
                return c - 1;
            }
            return c;
        })
    }, [])

    const [display, setDisplay] = useState(false);

    const toggleHandler = useCallback(() => {
        setPanels(inconsPanels())
        setDisplay(display => !display);
    }, [setDisplay, setPanels, inconsPanels])

    useEffect(() => {
        toggle.current = toggleHandler
    }, [toggle])

    return (
        <div className="emoticons-container emoticons-popup" style={{ ...containerStyle, display: (display) ? 'block' : 'none' }}>
            <button className="btn-prev" style={sliderNavigationButton} onClick={prev}>
                <FontAwesomeIcon icon={faAngleDoubleLeft} style={iconStyle} />
            </button>
            <div className="emoticons-slider" style={slideContainer}>
                <div className="emoticons-slides" style={{ transform: `translateX(-${current * 210}px)`, transition: 'transform 0.2s' }}>
                    {inconPanels}
                </div>

            </div>
            <button className="btn-prev" style={sliderNavigationButton} onClick={next}>
                <FontAwesomeIcon icon={faAngleDoubleRight} style={iconStyle} />
            </button>
        </div>
    )
}

export default EmotiContainer;
