import React, { useMemo } from "react"
import { getApi } from "./../../../../../apis/apiProvider";


export function useRecordPlayer(){
    const api = useMemo(()=>{
        return getApi('RecordsStore');
    }, [getApi])

    function playback(id, playbackState){
        console.log(id, playbackState)
    }

    function seek(id, progress){
        console.log("seek ", id , progress)
    }

    return [playback, seek]
}
