import React from "react";
import {Form} from "react-bootstrap";
import "./record-search.scss"

const RecordSearch = props =>{
    const [query, setQuery] = useState("")

    const setQueryHandler = useCallback( event =>{
        setQuery(event.target.value)
    }, [setQuery])

    return (
        <div className="record-search-container">
            <Form.Control className="record-search-control" type="text" value={query} onChange={setQueryHandler}/>
        </div>
    )
}
