import React, { useEffect, useContext, useState, useRef, useCallback, useMemo } from "react";
import { connect } from "react-redux";
import { reqRecOfUser } from "./../../actions"
import RecordsList from "./../common/components/RecordList/RecordList";
import { useRecordPlayer } from "./../common/Hooks/useRecordPlayer";
import PlayerControls from "./../common/components/PlayerControls/PlayerControls";
import LayoutContext from "./../common/Layout/LayoutContext";
import Pagin from "./../common/components/Pagin/Pagin";
import { DropdownButton, Dropdown } from "react-bootstrap";
import { useParams, useHistory, useLocation } from "react-router-dom";
import queryString from "query-string";
import "./user-records.scss"

const UserRecords = ({ requestList, userId, recordsList, countAll }) => {
    const [controls, player] = useRecordPlayer();
    //-------------------------
    const history = useHistory();
    const { user } = useParams();

    const queryStr = useLocation().search;
    let { pageSize, page, preloaded } = useMemo(() => {
        const values = queryString.parse(queryStr)
        return {
            pageSize: +values.pageSize,
            page: +values.page,
            preloaded: values.preloaded
        }
    }, [queryStr])
    const [_pageSize, setPageSize] = useState(20);
    const [_page, setPage] = useState(0);


    useEffect(() => {
        if (preloaded) { return }
        
        let pg = page || 0;
        let pgSize = pageSize || _pageSize;
 
        const where = {};

        if(user){
            where.nickname=user;
        } else if(userId) {
            where.userId = userId;
        }

        requestList(pgSize, pg, where);
        setPageSize(pgSize);
        setPage(pg)
        
    }, [userId, requestList, _pageSize, page, pageSize, preloaded])

    const layoutContext = useContext(LayoutContext);

    useEffect(() => {
        layoutContext.setFooter(false);
    }, [layoutContext])

    const changeTo = useCallback((pageSize) => {
        if (!userId) {
            return;
        }
        history.push('/my/records?pageSize=' + pageSize + "&page=" + _page);
        // requestList(userId, pageSize, _page);
    }, [history, _page, userId, requestList])

    const goTo = useCallback((page) => {
        if (!userId) {
            return;
        }
        history.push('/my/records?pageSize=' + _pageSize + "&page=" + page);
    }, [history, userId, _pageSize])

    return (
        <div className="user-records">
            <div className="user-records-top-bar">
                <h2 className="title">Your records</h2>
                <DropdownButton
                    alignRight
                    className="btn-pagger"
                    title={_pageSize + " on page "}>
                    <Dropdown.Item onClick={changeTo.bind(null, 10)}> 10 </Dropdown.Item>
                    <Dropdown.Item onClick={changeTo.bind(null, 20)}> 20 </Dropdown.Item>
                    <Dropdown.Item onClick={changeTo.bind(null, 30)}> 30 </Dropdown.Item>
                    <Dropdown.Item onClick={changeTo.bind(null, 50)}> 50 </Dropdown.Item>
                    <Dropdown.Item onClick={changeTo.bind(null, 75)}> 75 </Dropdown.Item>
                    <Dropdown.Item onClick={changeTo.bind(null, 100)}> 100 </Dropdown.Item>
                </DropdownButton>
            </div>

            <RecordsList
                list={recordsList}
                controls={controls}
                player={player} />
            <Pagin
                current={_page}
                call={goTo}
                all={Math.floor(countAll / _pageSize)} />
            <PlayerControls
                list={recordsList}
                controls={controls}
                player={player} />
        </div>
    )
}

const mapStateToProps = state => ({
    recordsList: state.records.list,
    countAll: state.records.countAll,
    userId: state.user.dbId,
})

const mapDispatchToProps = dispatch => ({
    requestList: (pageSize, page, where) => dispatch(reqRecOfUser(pageSize, page , where))
})

export default connect(mapStateToProps, mapDispatchToProps)(UserRecords)
