import {put, takeEvery} from 'redux-saga/effects';
import {ACTIONS} from "../actions/actions";

function *searchAsync(){
    console.log('searching async')
}

function* rootSaga(){
    takeEvery(ACTIONS.SEARCH_INPUT, searchAsync );
    yield console.log('helow from root saga')
}

export default rootSaga;
