import React, { useMemo } from "react";
import {connect} from "react-redux";
import useUrlParamSize from "./useUrlParamSize";
import style from "./user-profile.scss"

const UserProfile  = ({nickname, picture}) =>{
    const [urlWithSize] = useUrlParamSize();

    const pirctureUrl = useMemo(()=>{
        const size = {
            width : +style.userPictureWidth,
            height: +style.userPictureHeight,
        }
        return urlWithSize(picture, size)
    },[picture])

    return (
        <div className="user-profile">
            <div className="user-picture">
                <img src={pirctureUrl} alt="User picture"/>
            </div>
            <div className="user-nickname">
                {nickname}
            </div>
            <div className="user-description">
                <p>Profile descirption, i'm a dj not a cat</p>
            </div>
            <div className="user-record-stat">
                <span>2 records</span>
                <span>230 minut of miusic</span>
            </div>
            <div className="generes">
                <span>Generes:</span>
                <ul>
                    <li>Techno</li>
                    <li>Minimal</li>
                </ul>
            </div>
        </div>
    )
}

const mapStateToProps = state => ({
    picture : state.user.picture,
    nickname: state.user.nickname,
})

export default connect(mapStateToProps)(UserProfile);
