import React , { useState, useCallback, useEffect, useMemo, useContext } from "react";
import {PlayerContext} from "./../../useRecordPlayer";
import BinaryButton from "./../../../BinnaryButton/BinnaryButton"
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faPlay, faPause } from '@fortawesome/free-solid-svg-icons'
import classNames from "classnames"
import "./playback-button.scss"

export const PLAYBACK_STATE = {
    PLAY : "play music",
    PAUSE: "pause music"
}

const PlaybackButton = ({onChange, id}) => {
    const playerContext = useContext(PlayerContext);
    const [state, setState] = useState(PLAYBACK_STATE.PAUSE);
    const [icon, setIcon] = useState(faPause);

    const stateChange = useCallback(()=>{
        const nextState = (state === PLAYBACK_STATE.PLAY) ? PLAYBACK_STATE.PAUSE : PLAYBACK_STATE.PLAY;
        console.log(state, nextState)
        setState(nextState);
        if(onChange){
            onChange(nextState)
        }
    }, [setState, onChange])

    useEffect(()=>{
        switch(state){
            case PLAYBACK_STATE.PLAY : {
                setIcon(faPause)
                break;
            }
            case PLAYBACK_STATE.PAUSE: {
                setIcon(faPlay);
                break;
            }
        }
    }, [state, setIcon])


    useEffect(()=>{
        playerContext.setBtnCtrlHandler(id, (s) => setState(s))
    }, [playerContext, id])


    const btnClassNames = useMemo(()=>{
        return classNames(
            "playback-button",
            {
                "playback-button--active": (state === PLAYBACK_STATE.PLAY)
            }
        )
    }, [state])

    return (
        <div className="playback-button-container">
            <BinaryButton className={btnClassNames} value={state}  onChange={stateChange}>
                <FontAwesomeIcon icon={icon}/>
            </BinaryButton>
        </div>
    )
}

export default PlaybackButton
