import React, {useRef, useEffect} from "react";
import WaveSurfer from "wavesurfer";
import style from "./wave-surfer-player.scss"

const WaveSurferPlayer = ({peaks}) => {
    const container = useRef(null)

    const ws = useCallback(()=>{
        return new Promise((res, rej)=> {
            if(container.current && peaks){
                const ws = WaveSurfer.create({
                   container: container.current,
                   barWidth: 5,
                   height: 100,
                   waveColor: style.waveColor,
                   progressColor: style.progressColor,
               })
               res(ws)
              // ws.load(null, peaks);
              } else rej(null)
        })
    }, [container])

    useEffect(()=>{
        ws.then( ws => {
            ws.load((null, peaks))
        })
    }, [peaks, url])



    return (
        <div className="ws-player-container" ref={container}/>
    )
}

export default WaveSurferPlayer
