import React, { useState, useCallback } from "react";
import {connect} from "react-redux";
import {Form, Button} from "react-bootstrap";
import {useRecordPlayer} from "./../../Hooks/useRecordPlayer";
import "./record-comments.scss";


const RecordComments = ({recordId, commentList, postComment, deleteComment}) =>{
    const [, player] = useRecordPlayer();
    const [comment, setComment] = useState("")
    const [showButton, setShowButton] = useState(false);

    const updateComment = useCallback((e)=>{
        setComment(e.target.value);
        if(e.target.value === ""){
            setShowButton(false)
        }
    },[setComment, setShowButton])

    const onBlur = useCallback(()=>{
        (comment === "") ? setShowButton(false) : setShowButton(true);
    },[comment, setShowButton])

    const submitComment = useCallback((e) => {
        e.preventDefault();

        console.log(comment);

        setComment("")
        setShowButton(false)
    },[comment, postComment, setComment, setShowButton])

    return (
        <div className="record-comments">
            <Form onSubmit={submitComment}
                onFocus={setShowButton.bind(null, true)}
                onBlur={onBlur}>
                <Form.Group>
                    <Form.Label>
                        Post your comment:
                    </Form.Label>
                    <Form.Control 
                        className="record-comments-control"
                        type="textarea" 
                        as="textarea"
                        name="comment-conntent"
                        value={comment}

                        onChange={updateComment}/>
                </Form.Group>
                {showButton && <Button type="submit">Post</Button>}
            </Form>
                       
        </div>
    )
}
const mapStateToProps = state => ({

})

const mapDispatchToProps = dispatch => ({

})

export default connect(mapStateToProps, mapDispatchToProps)(RecordComments)
