import React, { useEffect , useRef} from "react";
import { connect } from "react-redux";
import "./deck.scss";
//import WaveSurfer from "wavesurfer";

//import WaveSurfer from "./WaveSurfer/WaveSurfer";

import { analyze } from 'web-audio-beat-detector';

import youTubeApi from "./../../apis/yt/ytApi";
import Player from "./Player/Player";
import TrackInfo from "./TrackInfo/TrackInfo";
import PlayBackControls from "./PlayBackControls/PlayBackControls";
import PitchControl from "./PitchControl/PitchControl";

const getApi = (apiName) =>{
    switch(apiName){
        case "YouTube": return youTubeApi;

        default : return null;
    }
}

class Deck extends React.Component {
    constructor(){
        super();
    }


   

    render() {
        let api = getApi(this.props.track.source);
        let url = api && api.getUrl(this.props.track.id) || null;

        return (
            <div className={ "deck " + "deck-" + this.props.name } >
                <TrackInfo name={ this.props.name } />
                <div className="flex-container">
                    <Player name={ this.props.name } url={url} />
                    <PitchControl name={ this.props.name } />
                </div>
                <PlayBackControls name={ this.props.name }/>
            </div>
            )
        }
    
}

const mapStateToProps = (state, ownProps) => ({
    track : state.console.deck[ownProps.name].track,
    state : state.console.deck[ownProps.name].state,
})

const mapDispachToProps = dispach => ({
    
})

export default connect(mapStateToProps)(Deck);
