import React, { useCallback, useMemo , useState, useContext} from "react"
import PlaybackButton, { PLAYBACK_STATE } from "./PlaybackButton/PlaybackButton";
import GenereList from "./GenereList/GenereList";
import WaveSurrferPlayer from "./WaveSurrferPlayer/WaveSurferPlayer";
import { Link } from "react-router-dom";
import {PlayerContext} from "./../useRecordPlayer";

const RecordListItem = ({ item , seekHandler, playbackHandler }) => {
    console.log(item)


    const onPlaybackChange = useCallback((state)=> {
        if(playbackHandler){
            playbackHandler(item.id , state);
        }
    }, [playbackHandler, item])


    return (
        <li className="record-list-item">
            <header>
                <nav>
                    <Link to={"/my/records/" + item.title} >
                        <h6 className="record-title">{item.title}</h6>
                    </Link>
                </nav>
                <aside>
                    <GenereList list={item.genere} />
                </aside>
            </header>
            <main>
                <PlaybackButton onChange={onPlaybackChange} id={item.id}/>
                <WaveSurrferPlayer peaks={item.peaks} seekHandler={seekHandler} id={item.id}/>
            </main>
        </li>
    )
}

export default RecordListItem
