import React from "react";

export default withMousePosition = WrappedComponent => {

  return class extends React.Component {
    handleMousePostion(event){
        console.log(event)
    }

    render() {
      return <WrappedComponent onMouseOver={this.handleMousePostion} {...this.props} />;
    }
  };
};
