import React, {useCallback, useEffect, useRef, useState} from "react";
import UUID from "uuidjs";
import style from "./slider.scss"

const Slider = ({slides, next, prev, minSlideWidth = style.minSlideWidth}) => {
    const container = useRef();
    const _containerWidth = useRef();
    const [visibleSlides, setVisibleSlides] = useState(null);
    const [slideWidth, setSlideWidth] = useState(null);

    const containerWidth = useCallback(()=>{
        if(!container.current) return 0;
        if(!_containerWidth.current){
            _containerWidth.current = container.current.parentElement.getBoundingClientRect().width;
        }
        return  _containerWidth.current;
    }, [_containerWidth])

    useEffect(()=> {
        if(!containerWidth() || !minSlideWidth) return;
        const slides = ~~(containerWidth() / minSlideWidth)
        setVisibleSlides(slides);
        setSlideWidth(containerWidth() / slides);
    }, [containerWidth, minSlideWidth, setVisibleSlides, setSlideWidth])

    console.log(containerWidth(), visibleSlides, slideWidth,  parseInt(minSlideWidth))
    return (
        <ul className="carousel-slider" ref={container}>
            {slides && slides instanceof Array && slides.map( slide =>(
                <li key={UUID.genV1()} className={"slide"} style={{width: slideWidth || "initial"}}>{slide}</li>
            ))}
        </ul>
    )
}
export default  Slider;
